import React from 'react';
import { BrowserRouter } from 'react-router-dom';
import { Route, Switch } from 'react-router-dom';
import NavBar from './components/NavBar';
import Routes from './components/Routes';
import 'bootstrap/dist/css/bootstrap.min.css';
import useLocalStorage from './hooks/useLocalStorage';
import MusicMatchApi from './api/api';
import './App.css';

export const TOKEN_STORAGE_ID = "music_match-token";

function App() {
  const [token, setToken] = useLocalStorage(TOKEN_STORAGE_ID);
  const [currentUser, setCurrentUser] = useState(null);
  const [infoLoaded, setInfoLoaded] = useState(true);


  useEffect(function loadUserInfo() {
    async function getCurrentUser() {
      if (token) {
        try {
          let { username } = jwt_decode(token);
          console.log(token);
          JoblyApi.token = token;
          currentUser = await JoblyApi.getCurrentUser(username);
          setCurrentUser(currentUser);
        } catch (err) {
          console.error('App function loadUserInfo encountered a problem loading the user.', err);
          setCurrentUser(null);
        }
      }
      setInfoLoaded(true);
    }

    setInfoLoaded(false);
    getCurrentUser();
  }, []);

  async function signup(signupData) {
    try {
      let token = await MusicMatchApi.signup(signupData);
      setToken(token);
      return { success: true };
    } catch (errors) {
      console.error('signup failed', errors);
      return { succes: false, errors };
    }
  }

  async function login(loginData) {
    try {
      let token = await MusicMatchApi.login(loginData);
      setToken(token);
      return { success: true };
    } catch (errors) {
      console.error('login failed', errors);
      return { success: false, errors };
    }
  }

  return (
    <BrowserRouter>
      <div className="App">
        <NavBar />
        <Routes signup={signup} login={login} />

      </div>
    </BrowserRouter>
  );
}

export default App;
