import React, { useState, useEffect } from 'react';
import useAuth from '../useAuth';
import SpotifyWebApi from 'spotify-web-api-node';
import Playlist from './Playlist';
import NewReleases from './NewReleases';
import { Container } from 'react-bootstrap';
import SearchPage from './SearchPage';
import Player from './Player';
import useLocalStorage from '../hooks/useLocalStorage';
import { Link } from 'react-router-dom';

const AUTH_URL = "https://accounts.spotify.com/authorize?client_id=04306bc8614e44bbb0f6f8071bb81e01&response_type=code&redirect_uri=http://localhost:3000&scope=streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state";

const code = new URLSearchParams(window.location.search).get('code');

const SpotifyApi = new SpotifyWebApi({
  clientId: '04306bc8614e44bbb0f6f8071bb81e01',
})

function Dashboard({ code }) {
  const accessToken = useAuth(code);
  const [playlists, setPlaylists] = useState([]);
  const [playingSong, setPlayingSong] = useState();
  const [newReleases, setNewReleases] = useState([]);

  localStorage.setItem('accessCode', code)
  // const storedCode = localStorage.getItem('accessCode');
  // console.log(storedCode)

  console.log('working code:', accessToken);

  function choosePlaylist(playlist) {
    setPlayingSong(playlist);
  }
  function chooseNewRelease(album) {
    setPlayingSong(album);
  }

  useEffect(() => {
    if (!accessToken) return;
    SpotifyApi.setAccessToken(accessToken)
  }, [accessToken]);


  useEffect(() => {
    if (!accessToken) return;
    SpotifyApi.getNewReleases({ limit: 13, offset: 0, country: 'US' })
      .then(res => {
        console.log(res.body)
        setNewReleases(res.body.albums.items.map(album => {

          return {
            name: album.name,
            uri: album.uri,
            albumUrl: album.images[0].url
          }
        }))
      }, function (err) {
        console.log("Something went wrong!", err);
      });
  }, [accessToken]);


  useEffect(() => {
    if (!accessToken) return;
    SpotifyApi.getPlaylistsForCategory('party', {
      country: 'US',
      limit: 10,
      offset: 0
    })
      .then(res => {
        console.log(res.body)
        setPlaylists(res.body.playlists.items.map(playlist => {

          return {
            name: playlist.name,
            description: playlist.description,
            uri: playlist.uri,
            albumUrl: playlist.images[0].url
          }
        }))
      }, function (err) {
        console.log("Something went wrong!", err);
      });
  }, [accessToken]);

  return (
    <div>
      <div>
        <div className="top-row">
          <Container className="flex-row"   >
            {playlists.map(playlist => (
              <Playlist playlist={playlist} key={playlist.uri} choosePlaylist={choosePlaylist} />
            ))}
          </Container>
        </div>
        <div>
          <Container className="flex-row"  >
            {newReleases.map(album => (
              <NewReleases album={album} key={album.uri} chooseNewRelease={chooseNewRelease} />
            ))}
          </Container >
        </div>
        <a id="search-btn" className="btn btn-success btn-lg display-flex" href='/searc'>Search Music + Lyrics</a>
        {/* <Link to="/search">
          Search Songs & Lyrics
          {/* {currentUser.first_name || currentUser.username} */}
        {/* </Link> */} */}
      </div>
      <br></br>
      <div className="music-player">
        <Player accessToken={accessToken} trackUri={playingSong?.uri} />
      </div>
    </div >
  )
}

export default Dashboard;
