import React, { useState, useEffect } from 'react';
import useAuth from '../useAuth';
import SpotifyWebApi from 'spotify-web-api-node';
import Playlist from './Playlist';
import { Container } from 'react-bootstrap';
import SearchPage from './SearchPage';

const SpotifyApi = new SpotifyWebApi({
  clientId: '04306bc8614e44bbb0f6f8071bb81e01',
})

const AUTH_URL = "https://accounts.spotify.com/authorize?client_id=04306bc8614e44bbb0f6f8071bb81e01&response_type=code&redirect_uri=http://localhost:3000&scope=streaming%20user-read-email%20user-read-private%20user-library-read%20user-library-modify%20user-read-playback-state%20user-modify-playback-state";

const code = new URLSearchParams(window.location.search).get('code');

function Dashboard({ code }) {
  const accessToken = useAuth(code);
  const [playlists, setPlaylists] = useState([]);
  console.log(playlists);
  // const [loading, setLoading] = useState(true);
  // console.log('playlists:', playlists);

  useEffect(() => {
    if (!accessToken) return;
    SpotifyApi.setAccessToken(accessToken)
  }, [accessToken]);

  // console.log(accessToken);

  // useEffect(() => {
  //   if (!accessToken) return;
  //   SpotifyApi.getPlaylistsForCategory('party', {
  //     country: 'BR',
  //     limit: 10,
  //     offset: 0
  //   })
  //     .then(function (data) {
  //       let output = data.body.playlists.items.map(item => item.images[0].url);
  //       setPlaylists(output);
  //       // console.log(output);
  //       // let output = data
  //       // console.log(output);

  //     }, function (err) {
  //       console.log("Something went wrong!", err);
  //     });
  // }, [accessToken]);

  useEffect(() => {
    if (!accessToken) return;
    SpotifyApi.getPlaylistsForCategory('party', {
      country: 'US',
      limit: 20,
      offset: 0
    })
      .then(res => {
        console.log(res.body)
        setPlaylists(res.body.playlists.items.map(playlist => {
          // const smallestPlaylistImage = playlist.images.reduce((smallest, image) => {
          //   if (image.height < smallest.height) return image
          //   return smallest
          // }, [])
          return {
            name: playlist.name,
            description: playlist.description,
            uri: playlist.uri,
            albumUrl: playlist.images[0].url
          }
        }))
        // console.log(res.body);
      }, function (err) {
        console.log("Something went wrong!", err);
      });
  }, [accessToken]);

  if (!playlists) {
    return 'loading playlists...'
    // } else if (code) {
    //   return (
    //     <SearchPage code={code} />
    //   )
  } else {
    return (
      <div>
        <div>
          <Container className="flex-row" style={{ width: "100%", height: "50%" }} >
            {playlists.map(playlist => (
              <Playlist playlist={playlist} key={playlist.uri} />
            ))}
          </Container >
          <a className="btn btn-success btn-lg" href={AUTH_URL}>Search Music</a>
        </div>
      </div>
    )
  }


}

export default Dashboard;
