  //   })
  //     .then(res => {
  //       console.log(res.body)
  //       setPlaylists(res.body.playlists.items.map(playlist => {

  //         return {
  //           name: playlist.name,
  //           description: playlist.description,
  //           uri: playlist.uri,
  //           albumUrl: playlist.images[0].url
  //         }
  //       }))
  //     }, function (err) {
  //       console.log("Something went wrong!", err);
  //     });
  // }, [accessToken]);

  // if (code) {
  //   return (
  //     <SearchPage code={code} />
  //   )
  // }

  return (
    <div>
      <div>
        <Container className="flex-row" style={{ width: "100%", height: "100%" }}  >
          {/* {playlists.map(playlist => (
            <Playlist playlist={playlist} key={playlist.uri} choosePlaylist={choosePlaylist} />
          ))} */}
          {/* {newReleases.map(album => (
            <NewReleases album={album} key={album.uri} chooseNewRelease={chooseNewRelease} />
          ))} */}
        </Container >
        <a id="search-btn" className="btn btn-success btn-lg display-flex" href='/loadsearch'>Search Music</a>
      </div>
      <br></br>
      <div className="music-player">
        <Player accessToken={accessToken} trackUri={playingSong?.uri} />
      </div>
    </div>
  )
}

export default Dashboard;
