import React, { useState, useEffect } from 'react';
import useAuth from '../useAuth';
import { Container, Form } from 'react-bootstrap';
import SpotifyWebApi from 'spotify-web-api-node';
import Song from './Song';
import Player from './Player';
import axios from 'axios';
import Image from './Image';

const SpotifyApi = new SpotifyWebApi({
  clientId: '04306bc8614e44bbb0f6f8071bb81e01',
})

function Homepage({ code }) {
  const accessToken = useAuth(code);
  const [search, setSearch] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [playingSong, setPlayingSong] = useState();
  const [lyrics, setLyrics] = useState('');
  // const [newReleases, setNewReleases] = useState([]);
  // const [isLoading, setLoading] = useState(true);

  // console.log(newReleases.url)



  function chooseSong(song) {
    setPlayingSong(song);
    setSearch('');
    setLyrics('');
  }

  useEffect(() => {
    if (!playingSong) return

    axios.get('http://localhost:3001/lyrics', {
      params: {
        track: playingSong.title,
        artist: playingSong.artist,
      }
    }).then(res => {
      setLyrics(res.data.lyrics);
    })
  }, [playingSong])

  useEffect(() => {
    if (!accessToken) return;
    SpotifyApi.setAccessToken(accessToken)
  }, [accessToken]);

  useEffect(() => {
    if (!search) return setSearchResults([])
    if (!accessToken) return;

    SpotifyApi.searchTracks(search).then(res => {

      setSearchResults(res.body.tracks.items.map(track => {
        const smallestAlbumImage = track.album.images.reduce((smallest, image) => {
          if (image.height < smallest.height) return image
          return smallest
        }, track.album.images[0])


        return {
          artist: track.artists[0].name,
          title: track.name,
          uri: track.uri,
          albumUrl: smallestAlbumImage.url
        }
      }))
    })
  }, [search, accessToken])

  // useEffect(() => {
  //   if (!accessToken) return;

  //   SpotifyApi.getFeaturedPlaylists({ limit: 10, offset: 1, country: 'US', locale: 'sv_SE', timestamp: '2014-10-23T09:00:00' })
  //     .then(function (data) {
  //       console.log(data.body.playlists.items)
  //       data.body.playlists.items.map((item) => {
  //         setNewReleases(item)
  //         // setLoading(false);
  //       })
  //     }, function (err) {
  //       console.log("Something went wrong!", err);
  //     });

  // }, []);

  // if (!newReleases) {
  //   return 'loading...'
  // }

  // console.log(newReleases.name);

  SpotifyApi.getPlaylistsForCategory('party', {
    country: 'BR',
    limit: 2,
    offset: 0
  })
    .then(function (data) {
      console.log(data.body);
    }, function (err) {
      console.log("Something went wrong!", err);
    });

  SpotifyApi.getNewReleases({ limit: 5, offset: 0, country: 'SE' })
    .then(function (data) {
      // console.log(data.body);
      // done();
      const output = data.body.items.map(item => item.href);
      console.log('output:', output);
    }, function (err) {
      console.log("Something went wrong!", err);
    });




  return (
    <Container className="d-flex flex-column py-2" style={{ height: "100vh" }} >
      <Form.Control type="search" placeholder="Search songs..." value={search} onChange={e => setSearch(e.target.value)} />
      <div className="flex-grow-1 my-2" style={{ overflowY: "auto" }}>
        {searchResults.map(song => (
          < Song song={song} key={song.uri} chooseSong={chooseSong} />
        ))}


        {/* <Image url={newReleases.images} name={newReleases.name} /> */}

        {searchResults.length === 0 && (
          <div className="text-center" style={{ whiteSpace: "pre" }}>
            {lyrics}
          </div>
        )}
      </div>
      <div>
        <Player accessToken={accessToken} trackUri={playingSong?.uri} />
      </div>
    </Container >
  )

}

export default Homepage;
